#!/usr/bin/env pwsh
<#
.SYNOPSIS
    Agent Skills Garden - Full Native PowerShell Installer
.DESCRIPTION
    Generates all 35+ skill files, the skill template, and root registry.yaml.
    Produces identical output to setup_skills.sh.
.PARAMETER Force
    Overwrite existing files. Without this flag, existing files are skipped.
.PARAMETER TargetDir
    Directory to generate files in. Defaults to script location.
.EXAMPLE
    .\setup_skills.ps1
    .\setup_skills.ps1 -Force
    .\setup_skills.ps1 -TargetDir "C:\my-project"
.NOTES
    Version: 2.0.0
    Standard: agentskills.io
    Requires: PowerShell 5.1+ or pwsh 7+
#>

[CmdletBinding()]
param(
    [switch]$Force,
    [string]$TargetDir = $PSScriptRoot
)

Set-StrictMode -Version Latest
$ErrorActionPreference = 'Stop'

Push-Location $TargetDir
try {

Write-Host ""
Write-Host "============================================" -ForegroundColor Cyan
Write-Host " Agent Skills Garden - PowerShell Installer" -ForegroundColor Cyan
Write-Host "============================================" -ForegroundColor Cyan
Write-Host ""

# -- Create directory structure --
$dirs = @(
    "skills/00-foundation",
    "skills/10-discovery",
    "skills/20-architecture",
    "skills/30-implementation",
    "skills/40-quality",
    "skills/50-performance",
    "skills/60-security",
    "skills/70-devops",
    "skills/80-docs",
    "skills/90-maintenance",
    "templates"
)

Write-Host "[1/4] Creating directory structure..." -ForegroundColor Yellow
foreach ($d in $dirs) {
    $dirPath = Join-Path $TargetDir ($d -replace '/', [IO.Path]::DirectorySeparatorChar)
    if (-not (Test-Path $dirPath)) {
        New-Item -ItemType Directory -Path $dirPath -Force | Out-Null
    }
}
Write-Host "  Done." -ForegroundColor Green

# -- Helper function --
function Write-SkillFile {
    param(
        [string]$RelativePath,
        [string]$Content
    )
    $fullPath = Join-Path $TargetDir ($RelativePath -replace '/', [IO.Path]::DirectorySeparatorChar)
    $parentDir = Split-Path $fullPath -Parent
    if (-not (Test-Path $parentDir)) {
        New-Item -ItemType Directory -Path $parentDir -Force | Out-Null
    }
    if ((Test-Path $fullPath) -and -not $Force) {
        Write-Host "  - $RelativePath (exists, use -Force to overwrite)" -ForegroundColor DarkYellow
        return
    }
    # Write UTF-8 without BOM
    $utf8NoBom = New-Object System.Text.UTF8Encoding $false
    [IO.File]::WriteAllText($fullPath, $Content, $utf8NoBom)
    Write-Host "  + $RelativePath" -ForegroundColor Green
}

Write-Host ""
Write-Host "[2/4] Generating skill files..." -ForegroundColor Yellow

# --- skills/00-foundation/auditor.md ---
$__b64 = ''
$__bytes = [Convert]::FromBase64String($__b64)
$__content = [System.Text.Encoding]::UTF8.GetString($__bytes)
Write-SkillFile -RelativePath 'skills/00-foundation/auditor.md' -Content $__content

